import React, { useState, useEffect, useRef } from 'react';
import { BarChart, Bar, PieChart, Pie, Cell, ResponsiveContainer, Tooltip, LineChart, Line, XAxis, YAxis, CartesianGrid, AreaChart, Area } from 'recharts';
import { CheckCircle, XCircle, Clock, PlayCircle, Upload, TrendingUp, Activity, Zap, Target, Award, AlertTriangle } from 'lucide-react';
import Papa from 'papaparse';
import './App.css';

const Dashboard = () => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [currentTime, setCurrentTime] = useState(new Date());
  const canvasRef = useRef(null);

  // Update time every second
  useEffect(() => {
    const timer = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  // Particle animation
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const particles = [];
    const particleCount = 50;

    class Particle {
      constructor() {
        this.x = Math.random() * canvas.width;
        this.y = Math.random() * canvas.height;
        this.vx = (Math.random() - 0.5) * 0.5;
        this.vy = (Math.random() - 0.5) * 0.5;
        this.size = Math.random() * 2 + 1;
        this.opacity = Math.random() * 0.5 + 0.2;
      }

      update() {
        this.x += this.vx;
        this.y += this.vy;

        if (this.x < 0 || this.x > canvas.width) this.vx *= -1;
        if (this.y < 0 || this.y > canvas.height) this.vy *= -1;
      }

      draw() {
        ctx.save();
        ctx.globalAlpha = this.opacity;
        ctx.fillStyle = '#ffffff';
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
        ctx.fill();
        ctx.restore();
      }
    }

    // Initialize particles
    for (let i = 0; i < particleCount; i++) {
      particles.push(new Particle());
    }

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      particles.forEach(particle => {
        particle.update();
        particle.draw();
      });
      requestAnimationFrame(animate);
    };

    animate();

    const handleResize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setLoading(true);
    setError(null);

    Papa.parse(file, {
      header: true,
      dynamicTyping: true,
      skipEmptyLines: true,
      complete: (results) => {
        if (results.data && results.data.length > 0) {
          const row = results.data[0];
          setData({
            totalCases: row['Total Test Cases'] || 144,
            totalExecuted: row['Total Executed'] || 132,
            totalPassed: row['Total Passed'] || 117,
            totalFailed: row['Total Failed'] || 6,
            inProgress: row['Total In Progress'] || 9,
            executionRate: parseFloat(row['% Execution']) || 91.67,
            passRate: parseFloat(row['% Passed']) || 88.64
          });
        }
        setLoading(false);
      },
      error: (err) => {
        setError('Failed to parse CSV file. Please check the file format.');
        setLoading(false);
      }
    });
  };

  // Sample data for demo
  const defaultData = {
    totalCases: 144,
    totalExecuted: 132,
    totalPassed: 117,
    totalFailed: 6,
    inProgress: 9,
    executionRate: 91.67,
    passRate: 88.64
  };

  const currentData = data || defaultData;

  // Animated Counter Component
  const AnimatedCounter = ({ value, duration = 2000 }) => {
    const [count, setCount] = useState(0);

    useEffect(() => {
      let start = 0;
      const end = parseInt(value);
      if (start === end) return;

      const totalMilSecDur = parseInt(duration);
      const incrementTime = (totalMilSecDur / end) > 100 ? 100 : (totalMilSecDur / end);

      const timer = setInterval(() => {
        start += 1;
        setCount(String(start));
        if (start === end) clearInterval(timer);
      }, incrementTime);

      return () => clearInterval(timer);
    }, [value, duration]);

    return <span>{count}</span>;
  };

  // Enhanced Metric Card Component
  const MetricCard = ({ title, value, subtitle, icon: Icon, iconColor, gradient, trend }) => (
    <div className={`metric-card ${gradient} animated-card`}>
      <div className="card-glow"></div>
      <div className="metric-header">
        <div className="metric-title-section">
          <span className="metric-title">{title}</span>
          {trend && <span className={`trend ${trend > 0 ? 'trend-up' : 'trend-down'}`}>
            {trend > 0 ? 'â†—' : 'â†˜'} {Math.abs(trend)}%
          </span>}
        </div>
        <div className={`metric-icon ${iconColor} pulse-animation`}>
          <Icon size={24} color="white" />
        </div>
      </div>
      <div className="metric-value">
        <AnimatedCounter value={value} />
      </div>
      {subtitle && <div className="metric-subtitle">{subtitle}</div>}
      <div className="progress-bar">
        <div className="progress-fill" style={{
          width: title.includes('PASSED') ? `${(value/currentData.totalExecuted)*100}%` : 
                 title.includes('EXECUTED') ? `${(value/currentData.totalCases)*100}%` : '100%'
        }}></div>
      </div>
    </div>
  );

  // Timeline data for area chart
  const timelineData = [
    { time: '00:00', passed: 20, failed: 2, progress: 5 },
    { time: '04:00', passed: 45, failed: 3, progress: 8 },
    { time: '08:00', passed: 70, failed: 4, progress: 7 },
    { time: '12:00', passed: 95, failed: 5, progress: 6 },
    { time: '16:00', passed: 110, failed: 6, progress: 4 },
    { time: '20:00', passed: 117, failed: 6, progress: 9 },
  ];

  if (loading) {
    return (
      <div className="dashboard">
        <canvas ref={canvasRef} className="particle-canvas"></canvas>
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <div className="loading-text">
            <Activity size={48} className="pulse-animation" />
            <h2>Initializing Dashboard...</h2>
            <p>Loading your test execution metrics</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <canvas ref={canvasRef} className="particle-canvas"></canvas>

      {/* Enhanced Header */}
      <div className="dashboard-header">
        <div className="header-background"></div>
        <div className="dashboard-icon floating-animation">
          <Activity size={64} color="white" />
        </div>
        <h1 className="dashboard-title gradient-text">
          Test Execution Dashboard
        </h1>
        <p className="dashboard-subtitle">
          Real-time Testing Metrics & Analytics
        </p>
        <div className="live-indicator">
          <div className="live-dot"></div>
          <span>Live â€¢ {currentTime.toLocaleTimeString()}</span>
        </div>

        <div className="upload-section">
          <label className="upload-button glass-button">
            <Upload size={20} />
            Upload New Dataset
            <input 
              type="file" 
              accept=".csv" 
              onChange={handleFileUpload}
              style={{ display: 'none' }}
            />
          </label>
        </div>
      </div>

      {error && (
        <div className="error glass-card">
          <XCircle size={24} />
          <p>{error}</p>
        </div>
      )}

      {/* Enhanced Metrics Grid */}
      <div className="metrics-grid">
        <MetricCard
          title="TOTAL TEST CASES"
          value={currentData.totalCases}
          icon={Target}
          iconColor="icon-purple"
          gradient="gradient-purple"
          trend={2.3}
        />

        <MetricCard
          title="TOTAL EXECUTED"
          value={currentData.totalExecuted}
          subtitle={`${currentData.executionRate.toFixed(2)}% Execution Rate`}
          icon={Zap}
          iconColor="icon-blue"
          gradient="gradient-blue"
          trend={5.7}
        />

        <MetricCard
          title="TOTAL PASSED"
          value={currentData.totalPassed}
          subtitle={`${currentData.passRate.toFixed(2)}% Pass Rate`}
          icon={Award}
          iconColor="icon-green"
          gradient="gradient-green"
          trend={1.2}
        />

        <MetricCard
          title="TOTAL FAILED"
          value={currentData.totalFailed}
          subtitle={`${((currentData.totalFailed / currentData.totalExecuted) * 100 || 0).toFixed(2)}% Failure Rate`}
          icon={AlertTriangle}
          iconColor="icon-red"
          gradient="gradient-red"
          trend={-0.8}
        />

        <MetricCard
          title="IN PROGRESS"
          value={currentData.inProgress}
          icon={Clock}
          iconColor="icon-orange"
          gradient="gradient-orange"
        />
      </div>

      {/* Enhanced Charts Section */}
      <div className="charts-section">
        <div className="chart-card glass-card">
          <h3 className="chart-title">Execution Timeline</h3>
          <ResponsiveContainer width="100%" height={350}>
            <AreaChart data={timelineData}>
              <defs>
                <linearGradient id="colorPassed" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#10B981" stopOpacity={0.8}/>
                  <stop offset="95%" stopColor="#10B981" stopOpacity={0.1}/>
                </linearGradient>
                <linearGradient id="colorFailed" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#EF4444" stopOpacity={0.8}/>
                  <stop offset="95%" stopColor="#EF4444" stopOpacity={0.1}/>
                </linearGradient>
              </defs>
              <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
              <XAxis dataKey="time" stroke="rgba(255,255,255,0.8)" />
              <YAxis stroke="rgba(255,255,255,0.8)" />
              <Tooltip 
                contentStyle={{
                  backgroundColor: 'rgba(0, 0, 0, 0.9)',
                  border: 'none',
                  borderRadius: '12px',
                  color: 'white',
                  backdropFilter: 'blur(10px)'
                }}
              />
              <Area type="monotone" dataKey="passed" stackId="1" stroke="#10B981" fill="url(#colorPassed)" />
              <Area type="monotone" dataKey="failed" stackId="1" stroke="#EF4444" fill="url(#colorFailed)" />
            </AreaChart>
          </ResponsiveContainer>
        </div>

        <div className="chart-card glass-card">
          <h3 className="chart-title">Test Distribution</h3>
          <ResponsiveContainer width="100%" height={350}>
            <PieChart>
              <Pie
                data={[
                  { name: 'Passed', value: currentData.totalPassed, color: '#10B981' },
                  { name: 'Failed', value: currentData.totalFailed, color: '#EF4444' },
                  { name: 'In Progress', value: currentData.inProgress, color: '#F59E0B' },
                  { name: 'Pending', value: currentData.totalCases - currentData.totalExecuted, color: '#6B7280' }
                ]}
                cx="50%"
                cy="50%"
                innerRadius={80}
                outerRadius={140}
                dataKey="value"
                animationBegin={0}
                animationDuration={2000}
              >
                {[
                  { name: 'Passed', value: currentData.totalPassed, color: '#10B981' },
                  { name: 'Failed', value: currentData.totalFailed, color: '#EF4444' },
                  { name: 'In Progress', value: currentData.inProgress, color: '#F59E0B' },
                  { name: 'Pending', value: currentData.totalCases - currentData.totalExecuted, color: '#6B7280' }
                ].map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={entry.color} />
                ))}
              </Pie>
              <Tooltip 
                contentStyle={{
                  backgroundColor: 'rgba(0, 0, 0, 0.9)',
                  border: 'none',
                  borderRadius: '12px',
                  color: 'white',
                  backdropFilter: 'blur(10px)'
                }}
              />
            </PieChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Enhanced Summary Section */}
      <div className="summary-section">
        <div className="summary-card glass-card">
          <h4>Performance Insights</h4>
          <div className="insights-grid">
            <div className="insight">
              <div className="insight-icon">ðŸŽ¯</div>
              <div className="insight-text">
                <strong>Execution Rate</strong>
                <span>{currentData.executionRate.toFixed(1)}%</span>
              </div>
            </div>
            <div className="insight">
              <div className="insight-icon">âœ…</div>
              <div className="insight-text">
                <strong>Success Rate</strong>
                <span>{currentData.passRate.toFixed(1)}%</span>
              </div>
            </div>
            <div className="insight">
              <div className="insight-icon">âš¡</div>
              <div className="insight-text">
                <strong>Efficiency</strong>
                <span>Excellent</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
