import React, { useState, useEffect, useRef, useMemo, useCallback } from 'react';
import { BarChart, Bar, PieChart, Pie, Cell, ResponsiveContainer, Tooltip, XAxis, YAxis, CartesianGrid } from 'recharts';
import { CheckCircle, XCircle, Clock, PlayCircle, Upload, TrendingUp, Activity, Zap, Target, Award, AlertTriangle } from 'lucide-react';
import Papa from 'papaparse';
import './App.css';

// Memoized Components for Better Performance
const AnimatedCounter = React.memo(({ value, duration = 1000 }) => {
  const [count, setCount] = useState(0);

  useEffect(() => {
    if (!value) return;

    let start = 0;
    const end = parseInt(value);
    if (start === end) return;

    const increment = end / (duration / 16); // 60fps
    const timer = setInterval(() => {
      start += increment;
      setCount(Math.floor(start));
      if (start >= end) {
        setCount(end);
        clearInterval(timer);
      }
    }, 16);

    return () => clearInterval(timer);
  }, [value, duration]);

  return <span>{count}</span>;
});

const MetricCard = React.memo(({ title, value, subtitle, icon: Icon, iconColor, gradient, trend }) => (
  <div className={`metric-card ${gradient} animated-card`}>
    <div className="card-glow"></div>
    <div className="metric-header">
      <div className="metric-title-section">
        <span className="metric-title">{title}</span>
        {trend && (
          <span className={`trend ${trend > 0 ? 'trend-up' : 'trend-down'}`}>
            {trend > 0 ? 'â†—' : 'â†˜'} {Math.abs(trend)}%
          </span>
        )}
      </div>
      <div className={`metric-icon ${iconColor}`}>
        <Icon size={24} color="white" />
      </div>
    </div>
    <div className="metric-value">
      <AnimatedCounter value={value} />
    </div>
    {subtitle && <div className="metric-subtitle">{subtitle}</div>}
  </div>
));

const Dashboard = () => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [currentTime, setCurrentTime] = useState(new Date());
  const canvasRef = useRef(null);
  const animationRef = useRef(null);

  // Memoized default data
  const defaultData = useMemo(() => ({
    totalCases: 144,
    totalExecuted: 132,
    totalPassed: 117,
    totalFailed: 6,
    inProgress: 9,
    executionRate: 91.67,
    passRate: 88.64
  }), []);

  const currentData = data || defaultData;

  // Optimized time update (reduced frequency)
  useEffect(() => {
    const timer = setInterval(() => setCurrentTime(new Date()), 5000); // Update every 5 seconds
    return () => clearInterval(timer);
  }, []);

  // Optimized particle animation with performance controls
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    let particles = [];
    const particleCount = window.innerWidth < 768 ? 20 : 30; // Reduce particles on mobile

    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    resizeCanvas();

    class Particle {
      constructor() {
        this.x = Math.random() * canvas.width;
        this.y = Math.random() * canvas.height;
        this.vx = (Math.random() - 0.5) * 0.3; // Reduced speed
        this.vy = (Math.random() - 0.5) * 0.3;
        this.size = Math.random() * 1.5 + 0.5; // Smaller particles
        this.opacity = Math.random() * 0.3 + 0.1; // Lower opacity
      }

      update() {
        this.x += this.vx;
        this.y += this.vy;
        if (this.x < 0 || this.x > canvas.width) this.vx *= -1;
        if (this.y < 0 || this.y > canvas.height) this.vy *= -1;
      }

      draw() {
        ctx.save();
        ctx.globalAlpha = this.opacity;
        ctx.fillStyle = '#ffffff';
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
        ctx.fill();
        ctx.restore();
      }
    }

    // Initialize particles
    for (let i = 0; i < particleCount; i++) {
      particles.push(new Particle());
    }

    let lastTime = 0;
    const targetFPS = 30; // Reduced FPS for better performance
    const frameDelay = 1000 / targetFPS;

    const animate = (currentTime) => {
      if (currentTime - lastTime >= frameDelay) {
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        particles.forEach(particle => {
          particle.update();
          particle.draw();
        });
        lastTime = currentTime;
      }
      animationRef.current = requestAnimationFrame(animate);
    };

    animationRef.current = requestAnimationFrame(animate);

    window.addEventListener('resize', resizeCanvas);

    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
      window.removeEventListener('resize', resizeCanvas);
    };
  }, []);

  // Memoized file upload handler
  const handleFileUpload = useCallback((event) => {
    const file = event.target.files[0];
    if (!file) return;

    setLoading(true);
    setError(null);

    Papa.parse(file, {
      header: true,
      dynamicTyping: true,
      skipEmptyLines: true,
      worker: true, // Use web worker for better performance
      complete: (results) => {
        if (results.data && results.data.length > 0) {
          const row = results.data[0];
          setData({
            totalCases: row['Total Test Cases'] || 144,
            totalExecuted: row['Total Executed'] || 132,
            totalPassed: row['Total Passed'] || 117,
            totalFailed: row['Total Failed'] || 6,
            inProgress: row['Total In Progress'] || 9,
            executionRate: parseFloat(row['% Execution']) || 91.67,
            passRate: parseFloat(row['% Passed']) || 88.64
          });
        }
        setLoading(false);
      },
      error: (err) => {
        setError('Failed to parse CSV file. Please check the file format.');
        setLoading(false);
      }
    });
  }, []);

  // Memoized chart data
  const timelineData = useMemo(() => [
    { time: '00:00', passed: 20, failed: 2, progress: 5 },
    { time: '04:00', passed: 45, failed: 3, progress: 8 },
    { time: '08:00', passed: 70, failed: 4, progress: 7 },
    { time: '12:00', passed: 95, failed: 5, progress: 6 },
    { time: '16:00', passed: 110, failed: 6, progress: 4 },
    { time: '20:00', passed: 117, failed: 6, progress: 9 },
  ], []);

  const pieData = useMemo(() => [
    { name: 'Passed', value: currentData.totalPassed, color: '#10B981' },
    { name: 'Failed', value: currentData.totalFailed, color: '#EF4444' },
    { name: 'In Progress', value: currentData.inProgress, color: '#F59E0B' },
    { name: 'Pending', value: currentData.totalCases - currentData.totalExecuted, color: '#6B7280' }
  ], [currentData]);

  if (loading) {
    return (
      <div className="dashboard">
        <canvas ref={canvasRef} className="particle-canvas"></canvas>
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <div className="loading-text">
            <Activity size={48} className="pulse-animation" />
            <h2>Initializing Dashboard...</h2>
            <p>Loading your test execution metrics</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <canvas ref={canvasRef} className="particle-canvas"></canvas>

      {/* Enhanced Header */}
      <div className="dashboard-header">
        <div className="header-background"></div>
        <div className="dashboard-icon floating-animation">
          <Activity size={64} color="white" />
        </div>
        <h1 className="dashboard-title gradient-text">
          Test Execution Dashboard
        </h1>
        <p className="dashboard-subtitle">
          Real-time Testing Metrics & Analytics
        </p>
        <div className="live-indicator">
          <div className="live-dot"></div>
          <span>Live â€¢ {currentTime.toLocaleTimeString()}</span>
        </div>

        <div className="upload-section">
          <label className="upload-button glass-button">
            <Upload size={20} />
            Upload New Dataset
            <input 
              type="file" 
              accept=".csv" 
              onChange={handleFileUpload}
              style={{ display: 'none' }}
            />
          </label>
        </div>
      </div>

      {error && (
        <div className="error glass-card">
          <XCircle size={24} />
          <p>{error}</p>
        </div>
      )}

      {/* Enhanced Metrics Grid */}
      <div className="metrics-grid">
        <MetricCard
          title="TOTAL TEST CASES"
          value={currentData.totalCases}
          icon={Target}
          iconColor="icon-purple"
          gradient="gradient-purple"
          trend={2.3}
        />

        <MetricCard
          title="TOTAL EXECUTED"
          value={currentData.totalExecuted}
          subtitle={`${currentData.executionRate.toFixed(2)}% Execution Rate`}
          icon={Zap}
          iconColor="icon-blue"
          gradient="gradient-blue"
          trend={5.7}
        />

        <MetricCard
          title="TOTAL PASSED"
          value={currentData.totalPassed}
          subtitle={`${currentData.passRate.toFixed(2)}% Pass Rate`}
          icon={Award}
          iconColor="icon-green"
          gradient="gradient-green"
          trend={1.2}
        />

        <MetricCard
          title="TOTAL FAILED"
          value={currentData.totalFailed}
          subtitle={`${((currentData.totalFailed / currentData.totalExecuted) * 100 || 0).toFixed(2)}% Failure Rate`}
          icon={AlertTriangle}
          iconColor="icon-red"
          gradient="gradient-red"
          trend={-0.8}
        />

        <MetricCard
          title="IN PROGRESS"
          value={currentData.inProgress}
          icon={Clock}
          iconColor="icon-orange"
          gradient="gradient-orange"
        />
      </div>

      {/* Enhanced Charts Section with BAR CHART */}
      <div className="charts-section">
        <div className="chart-card glass-card">
          <h3 className="chart-title">Execution Timeline</h3>
          <ResponsiveContainer width="100%" height={350}>
            <BarChart
              data={timelineData}
              margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
            >
              <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
              <XAxis 
                dataKey="time" 
                stroke="rgba(255,255,255,0.8)"
                fontSize={12}
              />
              <YAxis 
                stroke="rgba(255,255,255,0.8)"
                fontSize={12}
              />
              <Tooltip 
                contentStyle={{
                  backgroundColor: 'rgba(0, 0, 0, 0.9)',
                  border: 'none',
                  borderRadius: '12px',
                  color: 'white',
                  backdropFilter: 'blur(10px)'
                }}
              />
              <Bar 
                dataKey="passed" 
                fill="#10B981" 
                name="Passed Tests"
                radius={[4, 4, 0, 0]}
              />
              <Bar 
                dataKey="failed" 
                fill="#EF4444" 
                name="Failed Tests"
                radius={[4, 4, 0, 0]}
              />
              <Bar 
                dataKey="progress" 
                fill="#F59E0B" 
                name="In Progress"
                radius={[4, 4, 0, 0]}
              />
            </BarChart>
          </ResponsiveContainer>
        </div>

        <div className="chart-card glass-card">
          <h3 className="chart-title">Test Distribution</h3>
          <ResponsiveContainer width="100%" height={350}>
            <PieChart>
              <Pie
                data={pieData}
                cx="50%"
                cy="50%"
                innerRadius={80}
                outerRadius={140}
                dataKey="value"
                animationBegin={0}
                animationDuration={2000}
              >
                {pieData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={entry.color} />
                ))}
              </Pie>
              <Tooltip 
                contentStyle={{
                  backgroundColor: 'rgba(0, 0, 0, 0.9)',
                  border: 'none',
                  borderRadius: '12px',
                  color: 'white',
                  backdropFilter: 'blur(10px)'
                }}
              />
            </PieChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Enhanced Summary Section */}
      <div className="summary-section">
        <div className="summary-card glass-card">
          <h4>Performance Insights</h4>
          <div className="insights-grid">
            <div className="insight">
              <div className="insight-icon">ðŸŽ¯</div>
              <div className="insight-text">
                <strong>Execution Rate</strong>
                <span>{currentData.executionRate.toFixed(1)}%</span>
              </div>
            </div>
            <div className="insight">
              <div className="insight-icon">âœ…</div>
              <div className="insight-text">
                <strong>Success Rate</strong>
                <span>{currentData.passRate.toFixed(1)}%</span>
              </div>
            </div>
            <div className="insight">
              <div className="insight-icon">âš¡</div>
              <div className="insight-text">
                <strong>Efficiency</strong>
                <span>Excellent</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
